$(window).bind('scroll', function () {
    if (document.body.scrollTop > 25 || document.documentElement.scrollTop > 25) 
        {$('.navigation-clean').addClass('scroll');
        $('.navigation-clean .container').removeClass('scrollcontainer');
        }
     else {$('.navigation-clean').removeClass('scroll');
        $('.navigation-clean .container').addClass('scrollcontainer');
        }
});

